,p_process_name=>'UPDATE_COLLECTION'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    lco_collection_name constant varchar2(30 char) := ''QUILL_EMP_AGENDA_COLLECTION'';',
'    l_chosen_emp varchar2(100 CHAR) :=  :P32_EMPLOYEES;',
'    l_pic blob;',
'    l_email varchar2(50 char);',
'    l_name varchar2(50 char);',
'    l_mimetype varchar2(50 char);',
'    l_filename varchar2(100 char);',
'    l_seq_emp number;',
'begin',
'',
'    --call the max seq_id in case for gaps after delete;',
'    select max(seq_id) +1 into l_seq_emp from APEX_COLLECTIONS where COLLECTION_NAME =''QUILL_EMP_AGENDA_COLLECTION'';',
'',
'    if apex_application.g_x02 = ''agenda_starter'' then',
'        apex_collection.update_member(',
'            p_collection_name => lco_collection_name,',
'            p_seq => ''1'',',
