'            p_clob001 => apex_application.g_clob_01',
'        );',
'',
'    elsif apex_application.g_x02 = ''emp_profile_pic'' then',
'            --remove all blobs with seq id > 1',
'            for i in (select seq_id from apex_collections where collection_name =''QUILL_EMP_AGENDA_COLLECTION'')',
'            loop',
'                if i.seq_id > 1 then ',
'                        APEX_COLLECTION.DELETE_MEMBER(',
'                        p_collection_name => ''QUILL_EMP_AGENDA_COLLECTION'',',
'                        p_seq => i.seq_id);',
'                end if;',
'            end loop;',
'            ',
'            if apex_application.g_x03 is not null then',
'            --loop over chosen values and update collection',
'            for i in (SELECT trim(regexp_substr(apex_application.g_x03, ''[^:]+'', 1, LEVEL)) email FROM dual CONNECT BY LEVEL <= regexp_count(apex_application.g_x03, '':'')+1)',
'            loop',
'        ',
'                select filename, mimetype, content, employee_email, givenname || '' ''|| familyname as name',
'                into l_filename, l_mimetype, l_pic, l_email, l_name',
'                from cf_employees emp',
'                  JOIN cf_v_employees vemp ON (vemp.internalid = emp.user_mgm_id)',
'                where employee_email = i.email;',
'',
'                apex_collection.add_member(',
'                p_collection_name => lco_collection_name,',
'                p_c001 => ''emp_profile_pic'',',
'                p_c002 => l_filename,',
'                p_c003 => l_mimetype,',
'                p_c004 => l_email,',
'                p_c005 => l_name,',
'                p_blob001 => l_pic',
'                );',
'',
'                l_seq_emp := l_seq_emp +1;',
'            end loop;',
'            end if;',
'',
'    end if;',
'',
'',
'    htp.p(''{}'');',
'end;'))
,p_process_clob_language=>'PLSQL'
,p_required_patch=>wwv_flow_imp.id(5216523969377659)
);
